import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import './Interview.css';

interface Message {
  type: 'welcome' | 'transcript' | 'question' | 'interview_ended';
  text?: string;
  message?: string;
  is_final?: boolean;
  question_number?: number;
}

const Interview: React.FC = () => {
  const { sessionId } = useParams<{ sessionId: string }>();
  const [ws, setWs] = useState<WebSocket | null>(null);
  const [isConnected, setIsConnected] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [currentTranscript, setCurrentTranscript] = useState('');
  
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);

  // WebSocket –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ
  useEffect(() => {
    if (!sessionId) return;

    const websocket = new WebSocket(`ws://localhost:8000/ws/interview/${sessionId}`);
    
    websocket.onopen = () => {
      console.log('[WebSocket] –ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ');
      setIsConnected(true);
    };

    websocket.onmessage = (event) => {
      const message: Message = JSON.parse(event.data);
      console.log('[WebSocket] –ü–æ–ª—É—á–µ–Ω–æ:', message);
      
      switch (message.type) {
        case 'welcome':
          setMessages(prev => [...prev, message]);
          break;
          
        case 'transcript':
          if (message.is_final) {
            setCurrentTranscript('');
            setMessages(prev => [...prev, message]);
          } else {
            setCurrentTranscript(message.text || '');
          }
          break;
          
        case 'question':
          setMessages(prev => [...prev, message]);
          break;
          
        case 'audio_response':
          // –í–æ—Å–ø—Ä–æ–∏–∑–≤–æ–¥–∏–º –∞—É–¥–∏–æ –æ—Ç–≤–µ—Ç
          playAudioResponse((message as any).audio_data);
          break;
          
        case 'interview_ended':
          setMessages(prev => [...prev, message]);
          setIsRecording(false);
          break;
          
        default:
          setMessages(prev => [...prev, message]);
      }
    };

    websocket.onclose = () => {
      console.log('[WebSocket] –°–æ–µ–¥–∏–Ω–µ–Ω–∏–µ –∑–∞–∫—Ä—ã—Ç–æ');
      setIsConnected(false);
    };

    websocket.onerror = (error) => {
      console.error('[WebSocket] –û—à–∏–±–∫–∞:', error);
    };

    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, [sessionId]);

  // –ó–∞–ø—É—Å–∫ –∑–∞–ø–∏—Å–∏ –∞—É–¥–∏–æ
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
          
          // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –∞—É–¥–∏–æ —á–∞–Ω–∫ —á–µ—Ä–µ–∑ WebSocket (–∑–∞–≥–ª—É—à–∫–∞)
          if (ws && ws.readyState === WebSocket.OPEN) {
            ws.send(JSON.stringify({
              type: 'audio_chunk',
              data: `audio_chunk_${audioChunksRef.current.length}`
            }));
          }
        }
      };

      mediaRecorder.onstop = () => {
        // –°–∏–º—É–ª–∏—Ä—É–µ–º —Ñ–∏–Ω–∞–ª—å–Ω—ã–π —Ç—Ä–∞–Ω—Å–∫—Ä–∏–ø—Ç
        const finalText = `–¢–µ—Å—Ç–æ–≤—ã–π —Ç–µ–∫—Å—Ç ${Date.now()}`;
        if (ws && ws.readyState === WebSocket.OPEN) {
          ws.send(JSON.stringify({
            type: 'final_transcript',
            text: finalText
          }));
        }
        
        // –û—á–∏—â–∞–µ–º –∞—É–¥–∏–æ —á–∞–Ω–∫–∏
        audioChunksRef.current = [];
        
        // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –≤—Å–µ —Ç—Ä–µ–∫–∏
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start(100); // –ó–∞–ø–∏—Å—ã–≤–∞–µ–º —á–∞–Ω–∫–∏ –∫–∞–∂–¥—ã–µ 100–º—Å
      setIsRecording(true);
      
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞ –∫ –º–∏–∫—Ä–æ—Ñ–æ–Ω—É:', error);
      alert('–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –¥–æ—Å—Ç—É–ø –∫ –º–∏–∫—Ä–æ—Ñ–æ–Ω—É');
    }
  };

  // –û—Å—Ç–∞–Ω–æ–≤–∫–∞ –∑–∞–ø–∏—Å–∏
  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  // –ó–∞–≤–µ—Ä—à–µ–Ω–∏–µ –∏–Ω—Ç–µ—Ä–≤—å—é
  const endInterview = () => {
    if (ws && ws.readyState === WebSocket.OPEN) {
      ws.send(JSON.stringify({ type: 'end_interview' }));
    }
  };

  return (
    <div className="interview-container">
      <div className="interview-header">
        <h1>–ò–Ω—Ç–µ—Ä–≤—å—é - –°–µ—Å—Å–∏—è {sessionId}</h1>
        <div className="connection-status">
          –°—Ç–∞—Ç—É—Å: {isConnected ? 'üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ' : 'üî¥ –û—Ç–∫–ª—é—á–µ–Ω–æ'}
        </div>
      </div>

      <div className="interview-content">
        <div className="messages-section">
          <h3>–ò—Å—Ç–æ—Ä–∏—è —Å–æ–æ–±—â–µ–Ω–∏–π:</h3>
          <div className="messages-list">
            {messages.map((msg, index) => (
              <div key={index} className={`message message-${msg.type}`}>
                {msg.type === 'welcome' && (
                  <p><strong>–°–∏—Å—Ç–µ–º–∞:</strong> {msg.message}</p>
                )}
                {msg.type === 'transcript' && (
                  <p><strong>–í–∞—à –æ—Ç–≤–µ—Ç:</strong> {msg.text}</p>
                )}
                {msg.type === 'question' && (
                  <p><strong>–í–æ–ø—Ä–æ—Å {msg.question_number}:</strong> {msg.text}</p>
                )}
                {msg.type === 'interview_ended' && (
                  <p><strong>–°–∏—Å—Ç–µ–º–∞:</strong> {msg.message}</p>
                )}
              </div>
            ))}
          </div>
          
          {currentTranscript && (
            <div className="current-transcript">
              <p><strong>–°–µ–π—á–∞—Å –≥–æ–≤–æ—Ä–∏—Ç–µ:</strong> {currentTranscript}</p>
            </div>
          )}
        </div>

        <div className="controls-section">
          <div className="recording-controls">
            {!isRecording ? (
              <button 
                onClick={startRecording} 
                disabled={!isConnected}
                className="record-button"
              >
                üé§ –ù–∞—á–∞—Ç—å –∑–∞–ø–∏—Å—å
              </button>
            ) : (
              <button 
                onClick={stopRecording}
                className="stop-button"
              >
                ‚èπÔ∏è –û—Å—Ç–∞–Ω–æ–≤–∏—Ç—å –∑–∞–ø–∏—Å—å
              </button>
            )}
          </div>

          <div className="interview-controls">
            <button 
              onClick={endInterview}
              disabled={!isConnected}
              className="end-button"
            >
              ‚ùå –ó–∞–≤–µ—Ä—à–∏—Ç—å –∏–Ω—Ç–µ—Ä–≤—å—é
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Interview;
